</template>

<script>
	import {
		getSignList,
		userSign,
		getSignRule,
		apiSignLists,
		apiSign,
	} from "@/api/user";
	export default {
		data() {
			return {
				avatar: '', // 用户头像
				// nickname: '', // 用户昵称
				userIntegral: '', // 用户积分
				todaySign: '', // 用户今天是否已签到
				continuousDays: '', // 用户连续签到天数
				// 签到列表
				lists: [{
					day: '', // 天数
					integral: '', // 赠送积分数量
					is_sign: '', //	对应天数是否已签到	
				}],
				// 签到后返回数据
				totalIntegral: '', // 赠送积分数量
				days: '',// 连续签到次数
				
				earn: [] ,// 赚积分渠道
				
				showPop: false, // 签到弹窗
				
				isOpen: 0, // 是否开启签到功能 0-关闭 1-开启
			};
		},

		/**
		 * 生命周期函数--监听页面加载
		 */
		onLoad: function(options) {
			// this.userSignFun = trottle(this.userSignFun, 1000, this)
		},


		onShow: function() {
			this.getSignLists()
		},

		methods: {
			
			// 跳转到规则页面
			toSignRule() {
				uni.navigateTo({
					url: '/bundle/pages/sign_rule/sign_rule',
				})
			},
			
			// 跳转到积分明细页面
			toSignDetail() {
				uni.navigateTo({
					url: '/bundle/pages/sign_detail/sign_detail',
				})
			},
			
			onClose() {
				this.showPop = false
			},
			
			// 获取页面信息
			getSignLists() {
				apiSignLists()
					.then(res => {
						// console.log('hszzz', res)
						this.avatar = res.user.avatar
						this.continuousDays = res.user.continuous_days
						this.todaySign = res.user.today_sign
						this.userIntegral = res.user.user_integral

						this.lists = res.lists
						
						this.earn = res.earn
						
						this.isOpen = res.is_open
					})
			},
			
			// 点击签到
			userSignFun() {
				if (this.todaySign) {
					return;
				}

				apiSign()
				.then(res => {
					this.showPop = true;
					this.totalIntegral = res.total_integral
					this.days = res.days
									
					this.getSignLists()
				});
			}
		}
	}
</script>

